DisableLCD: ; 568
; don't need to do anything if lcd is already off
	ld a, [$ff40] ; LCDC
	bit 7, a ; lcd enable
	ret z
	
; reset ints
	xor a
	ld [$ff0f], a ; IF
	
; save enabled ints
	ld a, [$ffff] ; IE
	ld b, a
	
; disable vblank
	res 0, a ; vblank
	ld [$ffff], a ; IE
	
.wait
; wait until vblank
	ld a, [$ff44] ; LY
	cp 145 ; >144 (ensure beginning of vblank)
	jr nz, .wait
	
; turn lcd off
	ld a, [$ff40] ; LCDC
	and %01111111 ; lcd enable off
	ld [$ff40], a ; LCDC
	
; reset ints
	xor a
	ld [$ff0f], a ; IF
	
; restore enabled ints
	ld a, b
	ld [$ffff], a ; IE
	ret
; 58a

EnableLCD: ; 58a
	ld a, [$ff40] ; LCDC
