GetJoypadPublic: ; 984
; update mirror joypad input from $ffa4 (real input)

; $ffa6: released
; $ffa7: pressed
; $ffa8: input

; bit 0 A
;     1 B
;     2 SELECT
;     3 START
;     4 RIGHT
;     5 LEFT
;     6 UP
;     7 DOWN

	push af
	push hl
	push de
	push bc
	
; automated input?
	ld a, [InputType]
	cp a, $ff ; INPUT_AUTO
	jr z, .auto

; get input
	ld a, [$ffa4] ; real input
	ld b, a
	ld a, [$ffa8] ; last frame mirror
	ld e, a
	
; released
	xor b
	ld d, a
	and e
	ld [$ffa6], a
	
; pressed
	ld a, d
	and b
	ld [$ffa7], a
	
; leftover from pasted code
	ld c, a
	
; 
	ld a, b
	ld [$ffa8], a ; frame input
.quit
	pop bc
	pop de
	pop hl
	pop af
	ret	

.auto
; use predetermined input feed (used in catch tutorial)
; struct: [input][duration]

; save bank
	ld a, [$ff9d]
	push af
; 
	ld a, [AutoInputBank]
	rst $10
; 
	ld hl, AutoInputAddress ; AutoInputAddress-9
	ld a, [hli]
	ld h, [hl]
	ld l, a
	
; update when frame count hits 0
	ld a, [AutoInputLength]
	and a
	jr z, .updateauto
	
; until then, do nothing
	dec a
	ld [AutoInputLength], a
; restore bank
	pop af
	rst $10
; we're done
	jr .quit
	
.updateauto
; get input
	ld a, [hli]
; stop?
	cp a, $ff
	jr z, .stopinput
	ld b, a
	
; duration
	ld a, [hli]
	ld [AutoInputLength], a
; duration $ff = end at input
	cp a, $ff
	jr nz, .next
	
; no input
	dec hl
	dec hl
	ld b, $00 ; no input
	jr .finishauto
	
.next
; output recorded
	ld a, l
	ld [AutoInputAddress], a
	ld a, h
	ld [AutoInputAddress+1], a
	jr .finishauto
	
.stopinput
	call StopAutoInput
	ld b, $00 ; no input
	
.finishauto
; restore bank
	pop af
	rst $10
; update mirrors
	ld a, b
	ld [$ffa7], a ; pressed
	ld [$ffa8], a ; input
	jr .quit
; 9ee

StartAutoInput: ; 9ee
; start auto input stream at a:hl
; bank
	ld [AutoInputBank], a
; address
	ld a, l
	ld [AutoInputAddress], a
	ld a, h
	ld [AutoInputAddress+1], a
; don't wait to update
	xor a
	ld [AutoInputLength], a
; clear input mirrors
	xor a
	ld [$ffa7], a ; pressed
	ld [$ffa6], a ; released
	ld [$ffa8], a ; input
; start reading input stream instead of player input
	ld a, $ff ; INPUT_AUTO
	ld [InputType], a
	ret
; a0a

StopAutoInput: ; a0a
; clear autoinput ram
	xor a
	ld [AutoInputBank], a
	ld [AutoInputAddress], a
	ld [AutoInputAddress+1], a
	ld [AutoInputLength], a
; normal input
	ld [InputType], a
	ret
