GetFlag2: ; 80430
; uses bittable2
; takes flag id in de, mode in b
; can either check, set or reset a flag
; check: stores flag in c
; set/reset: no output
;
;    b = 0: reset flag
;      = 1: set flag
;      > 1: check flag
;
	ld a, d
	cp $00 ; is the flag id > 256?
	jr z, .start ; no
	jr c, .read ; carry is never set
	jr .invalid ; yes
.start
	ld a, e
	cp $a2 ; $a2 flag ids
	jr c, .read
.invalid
; uses flag 0
	xor a
	ld e, a
	ld d, a
.read
	ld hl, Flags
	add hl, de ; skip three
	add hl, de ; bytes per
	add hl, de ; flag
	ld e, [hl]
	inc hl
	ld d, [hl]
	inc hl
	ld c, [hl] ; flag bit
	ld a, b
	cp $01
	jr c, .reset ; b = 0
	jr z, .set   ; b = 1
	ld a, [de]   ; b > 1
	and c
	ld c, a
	ret
.set
	ld a, [de]
	or c
	ld [de], a
	ret
.reset
	ld a, c
	cpl ; AND all bits except the one in question
	ld c, a
	ld a, [de]
	and c
	ld [de], a
	ret
; 80462
	
