ReadNoiseSample: ; e85af
; sample struct:
;	[wx] [yy] [zz]
;	w: ? either 2 or 3
;	x: ? 0-7
;	zzyy: pointer to sample data
;		NOTE: these seem to have $4000 added to them later

	; de = NoiseSampleAddress
	ld hl, NoiseSampleAddress
	ld e, [hl]
	inc hl
	ld d, [hl]
	; is it empty?
	ld a, e
	or d
	jr z, .quit
	; get the noise sample
	ld a, [de]
	inc de
	; are we done?
	cp a, $ff
	jr z, .quit
	;
	and a, $0f ; bottom nybble
	inc a
	ld [$c2a2], a
	ld a, [de]
	inc de
	ld [$c293], a
	ld a, [de]
	inc de
	ld [$c294], a
	xor a
	ld [$c295], a
	;
	ld hl, NoiseSampleAddress
	ld [hl], e
	inc hl
	ld [hl], d
	ld hl, Channel1NoteFlags - Channel1
	add hl, bc
	set 4, [hl]
	ret
.quit
	ret
